import { Link, useSubmit } from "react-router-dom";
import classes from "./EventItem.module.css";

function EventItem({ event }) {
  const submit = useSubmit(); // to progamatically submit the request
  function startDeleteHandler() {
    const proceed = window.confirm("Are you sure ?");

    if (proceed) {
      submit(null, { method: "delete" });
    }
  }

  return (
    <article className="flex flex-col items-center gap-5">
      <img src={event.image} alt={event.title} className="w-96" />
      <div className="bg-black/70 p-5">
        <div className="flex justify-between  items-center my-5">
          <span className="capitalize font-medium text-4xl">{event.title}</span>
          <time className="font-medium text-xl">{event.date}</time>
        </div>
        <p className="text-left text-2xl font-thin">{event.description}</p>
        <menu className="my-10  flex justify-end gap-5 text-xl">
          <Link
            to="edit"
            className="text-amber-400 bg-gray-800 p-2 rounded-lg w-28"
          >
            Edit
          </Link>
          <button
            onClick={startDeleteHandler}
            className="text-red-400 bg-black/20 p-2 rounded-lg w-28"
          >
            Delete
          </button>
        </menu>
      </div>
    </article>
  );
}

export default EventItem;
