export async function customGet(
  url,
  successMsg = "Request Successed",
  failureMsg = "Request Failed"
) {
  try {
    const response = await fetch(url);
    const data = await response.json();
    return {
      success: true,
      data: data,
      message: successMsg,
    };
  } catch (error) {
    throw new Response(
      JSON.stringify({        message: `${failureMsg} : ${error.message}`,
      }),
      { status: 500 }
    );
  }
}
