import { json } from "react-router-dom";

export async function customGet(
  url,
  successMsg = "Request Successed",
  failureMsg = "Request Failed"
) {
  const response = await fetch(url);
  console.log("response : ", response);
  if (!response.ok) {
    return json(
      { message: `${failureMsg} : ${response.statusText}`},
      { status: response.status }
    );
  }
  const data = await response.json();
  console.log(data);
  return {
    success: true,
    data: data,
    message: successMsg,
  };
}
