import {
  useNavigate,
  Form,
  useNavigation,
  useActionData,
} from "react-router-dom";

import classes from "./EventForm.module.css";

function EventForm({ method, event }) {
  const data = useActionData();
  const navigate = useNavigate();
  const navigation = useNavigation();
  const isSubmitting = navigation.state === "submitting";
  function cancelHandler() {
    navigate("..");
  }

  return (
    // by providing action attribute we can trigger the action of out choice
    // we provide path to it.
    // <Form method="post" action="/events/new" className={classes.form}>
    <Form method="post" className={classes.form}>
      <p>
        <label htmlFor="title" className="text-base tracking-widest">Title</label>
        <input
          id="title"
          type="text"
          name="title"
          required
          className="h-10 text-sm capitalize font-thin"
          defaultValue={event ? event.title : ""}
        />
      </p>
      <p >
        <label className="text-base tracking-widest" htmlFor="image">Image</label>
        <input
          id="image"
          type="url"
          name="image"
          required
          defaultValue={event ? event.image : ""}
        />
      </p>
      <p className="text-base tracking-widest">
        <label htmlFor="date">Date</label>
        <input
          id="date"
          type="date"
          name="date"
          required
          defaultValue={event ? event.date : ""}
        />
      </p>
      <p className="text-base tracking-widest">
        <label htmlFor="description">Description</label>
        <textarea
          id="description"
          name="description"
          rows="5"
          required
          defaultValue={event ? event.description : ""}
        />
      </p>
      <div className={classes.actions}>
        <button type="button" onClick={cancelHandler} disabled={isSubmitting}>
          Cancel
        </button>
        <button disabled={isSubmitting}>
          {isSubmitting ? "Submitting" : "Save"}
        </button>
      </div>
    </Form>
  );
}

export default EventForm;
