import { Link, useSubmit } from "react-router-dom";
import classes from "./EventItem.module.css";

function EventItem({ event }) {
  /*
    we can submit data and trigger an action programmatically with the special use submit hook.
  */
  const submit = useSubmit();
  function startDeleteHandler() {
    const proceed = window.confirm("Are you sure ?");

    if (proceed) {
      // trigger an action to delete this event.
      // submit( <data we want to submit, it will automatically warpped in FORM data obj, at action function, const formData = await request.formData();> , { method: "DELETE", action:"/some-other-path" });
      submit(null, { method: "DELETE" });
    }
  }

  return (
    <article
      className={`${classes.event} flex flex-col items-center gap-5 w-[500px]`}
    >
      <img src={event.image} alt={event.title} />
      <div className="bg-black/30 p-5 w-full">
        <div className="flex justify-between  items-center mb-5">
          <span className="capitalize font-medium text-2xl">{event.title}</span>
          <time className="font-medium text-xl">{event.date}</time>
        </div>
        <p className="text-left text-base font-medium">{event.description}</p>
        <menu className="mt-10  flex justify-end gap-5 text-xl">
          <Link
            to="edit"
            className="text-amber-400 bg-black/70 p-2.5 rounded-full hover:scale-105 transition-all"
          >
            <svg
              xmlns="http://www.w3.org/2000/svg"
              fill="none"
              viewBox="0 0 24 24"
              strokeWidth={1.5}
              stroke="currentColor"
              className="w-5 h-5"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                d="m16.862 4.487 1.687-1.688a1.875 1.875 0 1 1 2.652 2.652L10.582 16.07a4.5 4.5 0 0 1-1.897 1.13L6 18l.8-2.685a4.5 4.5 0 0 1 1.13-1.897l8.932-8.931Zm0 0L19.5 7.125M18 14v4.75A2.25 2.25 0 0 1 15.75 21H5.25A2.25 2.25 0 0 1 3 18.75V8.25A2.25 2.25 0 0 1 5.25 6H10"
              />
            </svg>
          </Link>
          <button
            onClick={startDeleteHandler}
            className="text-red-400 bg-black/70 p-2.5 rounded-full hover:scale-105 transition-all"
          >
            <svg
              xmlns="http://www.w3.org/2000/svg"
              fill="none"
              viewBox="0 0 24 24"
              strokeWidth={1.5}
              stroke="currentColor"
              className="w-5 h-5"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                d="m14.74 9-.346 9m-4.788 0L9.26 9m9.968-3.21c.342.052.682.107 1.022.166m-1.022-.165L18.16 19.673a2.25 2.25 0 0 1-2.244 2.077H8.084a2.25 2.25 0 0 1-2.244-2.077L4.772 5.79m14.456 0a48.108 48.108 0 0 0-3.478-.397m-12 .562c.34-.059.68-.114 1.022-.165m0 0a48.11 48.11 0 0 1 3.478-.397m7.5 0v-.916c0-1.18-.91-2.164-2.09-2.201a51.964 51.964 0 0 0-3.32 0c-1.18.037-2.09 1.022-2.09 2.201v.916m7.5 0a48.667 48.667 0 0 0-7.5 0"
              />
            </svg>
          </button>
        </menu>
      </div>
    </article>
  );
}

export default EventItem;
